// Get all unique categories
export const getTextureCategories = () => {
  const categories = new Set(textures.map(t => t.category));
  return Array.from(categories);
};

// Get textures by category
export const getTexturesByCategory = (category) => {
  if (!category) return textures;
  return textures.filter(t => t.category === category);
};

// Get texture by slug
export const getTextureBySlug = (slug) => {
  return textures.find(t => t.slug === slug);
};

// Get random textures for recommendations
export const getRandomTextures = (excludeSlug, count = 3) => {
  const filtered = textures.filter(t => t.slug !== excludeSlug);
  const shuffled = [...filtered].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, count);
};

export default textures;
